import React from "react";
import emailjs from "emailjs-com";
import "./ContactForm.css";

const ContactForm = () => {
  function sendEmail(e) {
    e.preventDefault();

    emailjs
      .sendForm("YOUR_SERVICE_ID", "YOUR_TEMPLATE_ID", e.target, "YOUR_USER_ID")
      .then(
		  (result) => {
			  console.log(result.text);
        },
        (error) => {
			console.log(error.text);
        }
      );
  }

  return (
	  <div className="contact-form-container">
	  
	  <form class="contact-form validate-form">
					<span class="contact-form-title">
						Get in touch
					</span>

					<div class="input-wrapper validate-input" data-validate = "Name is required">
						<input class="input100" type="text" name="name" placeholder="Name" />
						<span class="focus-input100"></span>
						<span class="symbol-input100">
							<i class="fa fa-user" aria-hidden="true"></i>
						</span>
					</div>

					<div class="input-wrapper validate-input" data-validate = "Valid email is required: ex@abc.xyz">
						<input class="input100" type="text" name="email" placeholder="Email" />
						<span class="focus-input100"></span>
						<span class="symbol-input100">
							<i class="fa fa-envelope" aria-hidden="true"></i>
						</span>
					</div>

					<div class="input-wrapper validate-input" data-validate = "Message is required">
						<textarea class="input100" name="message" placeholder="Message"></textarea>
						<span class="focus-input100"></span>
					</div>

					<div class="container-contact100-form-btn">
						<button class="contact100-form-btn">
							Send
						</button>
					</div>
				</form>

				</div>
  );
};

export default ContactForm;
