import React from "react";
import emailjs from "emailjs-com";
import "./ContactForm.css";
import * as faIcons from "react-icons/fa";
import useForm from "./useForm";
import validateInfo from "./validateInfo";

const ContactForm = () => {
  function sendEmail(e) {
    e.preventDefault();

    emailjs
      .sendForm("YOUR_SERVICE_ID", "YOUR_TEMPLATE_ID", e.target, "YOUR_USER_ID")
      .then(
		  (result) => {
			  console.log(result.text);
        },
        (error) => {
			console.log(error.text);
        }
      );
  }
  const { handleChange, values, handleSubmit, errors} = useForm();
  return (
	  <div className="contact-form-container">
		  <div className="contact-form-wrapper">

	  <div className="form-anim"><h1>animación</h1></div>
	  <form class="contact-form validate-form" onSubmit={handleSubmit}>
					<h3 class="contact-form-title">
						Get in touch
					</h3>

					<div class="input-wrapper validate-input" data-validate = "Name is required">
						<input class="form-input" type="text" name="name" placeholder="Name" value={values.name} onChange={handleChange} />
				
						<span class="focus-form-input"></span>
						<span class="symbol-form-input">
							<faIcons.FaUser aria-hidden="true" /> 
						</span>
					</div>

					<div class="input-wrapper validate-input" data-validate = "Valid email is required: ex@abc.xyz">
						<input class="form-input" type="text" name="email" placeholder="Email" value={values.email} onChange={handleChange}/>
						<span class="focus-form-input"></span>
						<span class="symbol-form-input">
							<faIcons.FaEnvelope aria-hidden="true" />
						</span>
					</div>

					<div class="input-wrapper validate-input" data-validate = "Message is required">
						<textarea class="form-input" name="message" placeholder="Message"value={values.message} onChange={handleChange} />
						<span class="focus-form-input"></span>
					</div>

					<div class="form-btn-container">
						<button class="form-btn">
							Send
						</button>
					</div>
				</form>
		  </div>

				</div>
  );
};

export default ContactForm;
