import React from 'react'
import './ContactForm.css'
import emailjs from "emailjs-com";
import * as faIcons from "react-icons/fa";


const Form = () => {
    function sendEmail(e) {
		e.preventDefault();
	
		 emailjs
		   .sendForm("service_m2w8xot", "template_c996ynt", e.target, "user_AqqQ5LTNPx3tGeNSTeHj7")
		   .then(
			   (result) => {
				   console.log(result.text);
			 },
			 (error) => {
				 console.log(error.text);
			 }
					
		   );
		  handleClick();
	  }
	return (
            <form class="contact-form" onSubmit={sendEmail}>
					<h3 class="contact-form-title">
						Get in touch
					</h3>

					<div class="input-wrapper">
						<input class="form-input" type="text" name="name" placeholder="Name" required/>
				
						<span class="focus-form-input"></span>
						<span class="symbol-form-input">
							<faIcons.FaUser aria-hidden="true" /> 
						</span>
					</div>

					<div class="input-wrapper">
						<input class="form-input" type="text" name="email" placeholder="Email"  required/>
						<span class="focus-form-input"></span>
						<span class="symbol-form-input">
							<faIcons.FaEnvelope aria-hidden="true" />
						</span>
					</div>

					<div class="input-wrapper">
						<textarea class="form-input" name="message" placeholder="Message"  required/>
						<span class="focus-form-input"></span>
					</div>

					<div class="form-btn-container">
						<button class="form-btn">
							Send
						</button>
					</div>
				</form>    )
}

export default Form
